import pandas as pdimport osimport numpy as nproot_path = './result_csv/'pred_list = []print('读取所有预测数据')for dir in os.listdir(root_path):    file_path = os.path.join(root_path, dir)    print(file_path)    df = pd.read_csv(file_path)    pred_list.append(df['target'])pred_list = np.array(pred_list)print('计算均值')pred = np.mean(pred_list, axis=0)path = os.path.join(root_path, os.listdir(root_path)[0])df = pd.read_csv(path)df['target'] = predprint('保存')df.to_csv('%d_nn.csv' % pred_list.shape[0], index=False)